package Lec_01_Introduction_to_Java;

public class C_Print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int no = 12;
		double salary = 12323.233;
		float rate = 12.3f;
		String name = "Dananjaya";
		char letter = 'D';
		
		System.out.println("No is = "+no);
		System.out.println("Salary is = "+salary);
		System.out.println("Rate is = "+rate);
		System.out.println("Name is = "+name);
		System.out.println("Letter is = "+letter);
		
		//print in single line
		System.out.println("\nNo is "+no+".\nSalary is "+salary+".\nrate is "+rate+".\nName is "+name+".\nLetter is "+letter);

	}

}
